import React from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

class Profile extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            loggedInUser: this.props.match.params.id,
            userId: "",
            shows: [],
            id: 0,
            imgFile: null,
            imgURL: "",
            title: "",
            genre: ""
        }
    }
    componentDidMount() {
        this.getUserShows()
    }

    getUserShows = async () => {
        // console.log('here')
        const userId = this.props.match.params.id
        let userURL = `http://localhost:3010/shows/user/${userId}`
        let res = await axios.get(userURL)
        // console.log(res)
        this.setState({
            shows: res
        })
    }

    render() {
        const { shows } = this.state
        for (const el of shows) {
            console.log(el)
            let name = el.username
            let profile = el.avatar_url
            return (
                <div id="user">
                    <div id="user">
                        <h1>{name}</h1>
                        <img id="userImage" src={profile} alt="profile" key={name} />
                    </div>


                </div>
            )
        }
    }
}
    export default Profile;
